    31→    /// Request from client to server
    32→    Request {
    33→        id: String,
    34→        method: String,
    35→        params: serde_json::Value,
    36→    },
    37→    /// Response from server to client
    38→    Response {
    39→        id: String,
    40→        result: Option<serde_json::Value>,
    41→        error: Option<String>,
    42→    },
    43→    /// Notification (one-way message)
    44→    Notification {
    45→        method: String,
    46→        params: serde_json::Value,
    47→    },
    48→}
    49→
    50→impl VsockMessage {
    51→    /// Create a new request message
    52→    pub fn request(id: String, method: String, params: serde_json::Value) -> Self {
    53→        Self::Request { id, method, params }
    54→    }
    55→
    56→    /// Create a new response message
    57→    pub fn response(
    58→        id: String,
    59→        result: Option<serde_json::Value>,
    60→        error: Option<String>,
    61→    ) -> Self {
    62→        Self::Response { id, result, error }
    63→    }
    64→
    65→    /// Create a new notification message
    66→    pub fn notification(method: String, params: serde_json::Value) -> Self {
    67→        Self::Notification { method, params }
    68→    }
    69→
    70→    /// Serialize message to JSON
    71→    pub fn to_json(&self) -> Result<Vec<u8>> {
    72→        serde_json::to_vec(self).context("Failed to serialize vsock message")
    73→    }
    74→
    75→    /// Deserialize message from JSON
    76→    pub fn from_json(data: &[u8]) -> Result<Self> {
    77→        if data.len() > MAX_MESSAGE_SIZE {
    78→            anyhow::bail!("Message size exceeds limit");
    79→        }
    80→        serde_json::from_slice(data).context("Failed to deserialize vsock message")
    81→    }
    82→}
    83→
    84→/// Host-side vsock listener
    85→pub struct VsockHostListener {
    86→    socket_path: PathBuf,
    87→    listener: UnixListener,
    88→}
    89→
    90→impl VsockHostListener {
    91→    /// Create a new host listener bound to a Unix socket
    92→    pub async fn bind(socket_path: &str) -> Result<Self> {
    93→        let path = PathBuf::from(socket_path);
    94→
    95→        // Ensure parent directory exists
    96→        if let Some(parent) = path.parent() {
    97→            fs::create_dir_all(parent)
    98→                .await
    99→                .context("Failed to create socket directory")?;
   100→        }
   101→
   102→        // Remove existing socket file if it exists
   103→        if path.exists() {
   104→            fs::remove_file(&path)
   105→                .await
   106→                .context("Failed to remove existing socket file")?;
   107→        }
   108→
   109→        let listener = UnixListener::bind(&path).context("Failed to bind Unix socket")?;
   110→
   111→        Ok(Self {
   112→            socket_path: path,
   113→            listener,
   114→        })
   115→    }
   116→
   117→    /// Accept a new connection
   118→    pub async fn accept(&self) -> Result<VsockConnection> {
   119→        let (socket, _addr) = self
   120→            .listener
   121→            .accept()
   122→            .await
   123→            .context("Failed to accept connection")?;
   124→        Ok(VsockConnection::new(socket))
   125→    }
   126→
   127→    /// Get socket path
   128→    pub fn path(&self) -> &PathBuf {
   129→        &self.socket_path
   130→    }
   131→}
   132→
   133→/// Client-side vsock connector
   134→pub struct VsockClient {
   135→    socket_path: String,
   136→}
   137→
   138→impl VsockClient {
   139→    /// Create a new vsock client
   140→    pub fn new(socket_path: String) -> Self {
   141→        Self { socket_path }
   142→    }
   143→
   144→    /// Connect to the host socket
   145→    pub async fn connect(&self) -> Result<VsockConnection> {
   146→        let socket = UnixStream::connect(&self.socket_path)
   147→            .await
   148→            .context("Failed to connect to Unix socket")?;
   149→        Ok(VsockConnection::new(socket))
   150→    }
   151→}
   152→
   153→/// Active vsock connection
   154→pub struct VsockConnection {
   155→    stream: UnixStream,
   156→}
   157→
   158→impl VsockConnection {
   159→    /// Create a new connection wrapper
   160→    pub fn new(stream: UnixStream) -> Self {
   161→        Self { stream }
   162→    }
   163→
   164→    /// Send a message
   165→    pub async fn send(&mut self, message: &VsockMessage) -> Result<()> {
   166→        let data = message.to_json()?;
   167→        // Write length prefix (u32, big endian)
   168→        let len = data.len() as u32;
   169→        self.stream
   170→            .write_u32(len)
   171→            .await
   172→            .context("Failed to write message length")?;
   173→        // Write data
   174→        self.stream
   175→            .write_all(&data)
   176→            .await
   177→            .context("Failed to write message data")?;
   178→        self.stream.flush().await.context("Failed to flush stream")?;
   179→        Ok(())
   180→    }
   181→
   182→    /// Receive a message
   183→    pub async fn receive(&mut self) -> Result<VsockMessage> {
   184→        // Read length prefix
   185→        let len = self
   186→            .stream
   187→            .read_u32()
   188→            .await
   189→            .context("Failed to read message length")?;
   190→
   191→        if len as usize > MAX_MESSAGE_SIZE {
   192→            anyhow::bail!("Message size {} exceeds limit", len);
   193→        }
   194→
   195→        // Read data
   196→        let mut buffer = vec![0u8; len as usize];
   197→        self.stream
   198→            .read_exact(&mut buffer)
   199→            .await
   200→            .context("Failed to read message data")?;
   201→
   202→        VsockMessage::from_json(&buffer)
   203→    }
   204→}
