     1→// Seccomp Filters for Firecracker VMs
     2→//
     3→// Defense-in-depth security: syscall filtering prevents even compromised VMs
     4→// from executing dangerous operations. Blocks 99% of syscalls, allowing
     5→// only essential ones for basic VM operation.
     6→
     7→use anyhow::{Context, Result};
     8→use serde::{Deserialize, Serialize};
     9→use std::collections::{HashMap, VecDeque};
    10→use std::sync::Arc;
    11→use tokio::sync::RwLock;
    12→use tracing::{debug, info, warn};
    13→
    14→/// Seccomp filter level (security profile)
    15→#[derive(Debug, Clone, Copy, PartialEq, Eq, Serialize, Deserialize, Default)]
    16→pub enum SeccompLevel {
    17→    /// Minimal - allow only absolutely essential syscalls (most secure)
    18→    Minimal,
    19→    /// Basic - allow common operations (recommended)
    20→    #[default]
    21→    Basic,
    22→    /// Permissive - allow most syscalls (for testing only)
    23→    Permissive,
    24→}
    25→
    26→/// Syscall filtering rule
    27→#[derive(Debug, Clone, Serialize, Deserialize)]
    28→pub struct SyscallRule {
    29→    /// Syscall number or name
    30→    pub name: String,
    31→    /// Action to take (allow, deny, log)
    32→    pub action: SeccompAction,
    33→    /// Security rationale
    34→    pub rationale: String,
    35→}
    36→
    37→/// Seccomp action
    38→#[derive(Debug, Clone, Copy, PartialEq, Eq, Serialize, Deserialize)]
    39→pub enum SeccompAction {
    40→    /// Allow syscall
    41→    Allow,
    42→    /// Block syscall (returns EPERM)
    43→    Deny,
    44→    /// Allow but log for security monitoring
    45→    Log,
    46→}
    47→
    48→/// Seccomp filter configuration for a VM
    49→#[derive(Debug, Clone, Serialize, Deserialize)]
    50→pub struct SeccompFilter {
    51→    /// Filter level (determines whitelist)
    52→    pub level: SeccompLevel,
    53→    /// Custom syscall rules (overrides level defaults)
    54→    #[serde(default)]
    55→    pub custom_rules: Vec<SyscallRule>,
    56→    /// Enable audit logging
    57→    #[serde(default = "default_audit_enabled")]
    58→    pub audit_enabled: bool,
    59→}
    60→
    61→fn default_audit_enabled() -> bool {
    62→    true
    63→}
    64→
    65→impl Default for SeccompFilter {
    66→    fn default() -> Self {
    67→        Self {
    68→            level: SeccompLevel::Basic,
    69→            custom_rules: Vec::new(),
    70→            audit_enabled: true,
    71→        }
    72→    }
    73→}
    74→
    75→impl SeccompFilter {
    76→    /// Create a new seccomp filter with specified level
    77→    pub fn new(level: SeccompLevel) -> Self {
    78→        Self {
    79→            level,
    80→            ..Default::default()
    81→        }
    82→    }
    83→
    84→    /// Add a custom syscall rule
    85→    pub fn add_rule(mut self, rule: SyscallRule) -> Self {
    86→        self.custom_rules.push(rule);
    87→        self
    88→    }
    89→
    90→    /// Build the syscall whitelist based on filter level
    91→    pub(crate) fn build_whitelist(&self) -> Vec<&'static str> {
    92→        match self.level {
    93→            SeccompLevel::Minimal => self.minimal_whitelist(),
    94→            SeccompLevel::Basic => self.basic_whitelist(),
    95→            SeccompLevel::Permissive => self.permissive_whitelist(),
    96→        }
    97→    }
    98→
    99→    /// Minimal whitelist - absolute essentials for VM operation
   100→    fn minimal_whitelist(&self) -> Vec<&'static str> {
   101→        vec![
   102→            // I/O operations
   103→            "read",
   104→            "write",
   105→            "exit",
   106→            "exit_group",
   107→            // Memory management
   108→            "mmap",
   109→            "munmap",
   110→            "mprotect",
   111→            "brk",
   112→            // Signal handling
   113→            "rt_sigreturn",
   114→            "rt_sigprocmask",
   115→            // Basic filesystem
   116→            "fstat",
   117→            "stat",
   118→            "lseek",
   119→            "close",
   120→        ]
   121→    }
   122→
   123→    /// Basic whitelist - common operations (recommended for production)
   124→    fn basic_whitelist(&self) -> Vec<&'static str> {
   125→        let mut whitelist = self.minimal_whitelist();
   126→
   127→        // Additional safe syscalls
   128→        whitelist.extend(vec![
   129→            // Extended I/O
   130→            "readv",
   131→            "writev",
   132→            "pread64",
   133→            "pwrite64",
   134→            // File operations
   135→            "open",
   136→            "openat",
   137→            "access",
   138→            "faccessat",
   139→            "statfs",
   140→            "fstatfs",
   141→            // Time
   142→            "clock_gettime",
   143→            "gettimeofday",
   144→            // Process info (read-only)
   145→            "getpid",
   146→            "gettid",
   147→            "getppid",
   148→            // Scheduling
   149→            "sched_yield",
   150→            "sched_getaffinity",
   151→            // epoll for async I/O
   152→            "epoll_wait",
   153→            "epoll_ctl",
   154→            "epoll_pwait",
   155→            // Eventfd
   156→            "eventfd2",
   157→            // Basic signal handling
   158→            "sigaltstack",
   159→            // Pipe
   160→            "pipe",
   161→            "pipe2",
   162→            // Dup
   163→            "dup",
   164→            "dup2",
   165→            "dup3",
   166→            // Basic polling
   167→            "poll",
   168→            "ppoll",
   169→        ]);
   170→
   171→        whitelist
   172→    }
   173→
   174→    /// Permissive whitelist - for testing/debugging only
   175→    fn permissive_whitelist(&self) -> Vec<&'static str> {
   176→        let mut whitelist = self.basic_whitelist();
   177→
   178→        // Additional syscalls for testing
   179→        whitelist.extend(vec![
   180→            "uname",
   181→            "sysinfo",
   182→            "getrlimit",
   183→            "getrusage",
   184→            "getgroups",
   185→            "getegid",
   186→            "geteuid",
   187→            "getgid",
   188→            "getuid",
   189→            "arch_prctl",
   190→            "set_tid_address",
   191→            "set_robust_list",
   192→        ]);
   193→
   194→        whitelist
   195→    }
   196→
   197→    /// Convert to Firecracker JSON format
   198→    ///
   199→    /// Firecracker seccomp filter format:
   200→    /// ```json
   201→    /// {
   202→    ///   "seccomp": {
   203→    ///     "filter": "ALLOW",
   204→    ///     "args": [
   205→    ///       {
   206→    ///         "syscall_number": 0,
   207→    ///         "arg_filter": "EQ",
   208→    ///         "val": 0
   209→    ///       }
   210→    ///     ]
   211→    ///   }
   212→    /// }
   213→    /// ```
   214→    ///
   215→    /// However, Firecracker v1.14+ uses a simpler format where we specify
   216→    /// allowed syscalls by name/number.
   217→    pub fn to_firecracker_json(&self) -> Result<String> {
   218→        let whitelist = self.build_whitelist();
   219→
   220→        // Build seccomp filter in Firecracker format
   221→        // Note: Firecracker uses a predefined seccomp profile that we can customize
   222→        let filter = serde_json::json!({
   223→            "seccomp": {
   224→                "filter": "allow",
   225→                "args": whitelist
   226→            }
   227→        });
   228→
   229→        serde_json::to_string_pretty(&filter).context("Failed to serialize seccomp filter")
   230→    }
   231→}
