   233→/// Audit log entry for blocked syscalls
   234→#[derive(Debug, Clone, Serialize, Deserialize)]
   235→pub struct SeccompAuditEntry {
   236→    /// VM ID
   237→    pub vm_id: String,
   238→    /// Syscall name
   239→    pub syscall: String,
   240→    /// Timestamp
   241→    pub timestamp: chrono::DateTime<chrono::Utc>,
   242→    /// PID of process that attempted syscall
   243→    pub pid: u32,
   244→    /// Attack detected (multiple blocked syscalls from same VM)
   245→    pub attack_detected: bool,
   246→}
   247→
   248→/// Seccomp audit log manager
   249→#[derive(Debug, Clone)]
   250→pub struct SeccompAuditLog {
   251→    entries: Arc<RwLock<VecDeque<SeccompAuditEntry>>>,
   252→    /// Track repeated violations per VM (for attack detection)
   253→    violation_counts: Arc<RwLock<HashMap<String, usize>>>,
   254→}
   255→
   256→const MAX_SECCOMP_LOG_ENTRIES: usize = 10_000;
   257→
   258→impl Default for SeccompAuditLog {
   259→    fn default() -> Self {
   260→        Self {
   261→            entries: Arc::new(RwLock::new(VecDeque::new())),
   262→            violation_counts: Arc::new(RwLock::new(HashMap::new())),
   263→        }
   264→    }
   265→}
   266→
   267→impl SeccompAuditLog {
   268→    /// Create a new audit log
   269→    pub fn new() -> Self {
   270→        Self::default()
   271→    }
   272→
   273→    /// Log a blocked syscall
   274→    pub async fn log_blocked_syscall(&self, vm_id: &str, syscall: &str, pid: u32) -> Result<()> {
   275→        let entry = SeccompAuditEntry {
   276→            vm_id: vm_id.to_string(),
   277→            syscall: syscall.to_string(),
   278→            timestamp: chrono::Utc::now(),
   279→            pid,
   280→            attack_detected: false,
   281→        };
   282→
   283→        // Check for repeated violations (attack detection)
   284→        let mut counts = self.violation_counts.write().await;
   285→        let count = counts.entry(vm_id.to_string()).or_insert(0);
   286→        *count += 1;
   287→
   288→        let attack_detected = *count > 10; // Threshold for alert
   289→
   290→        // Log the entry
   291→        let mut entries = self.entries.write().await;
   292→        if entries.len() >= MAX_SECCOMP_LOG_ENTRIES {
   293→            entries.pop_front();
   294→        }
   295→        entries.push_back(entry);
   296→
   297→        if attack_detected {
   298→            warn!(
   299→                "⚠️  ATTACK DETECTED: VM {} has {} blocked syscalls - possible compromise",
   300→                vm_id, count
   301→            );
   302→        } else {
   303→            debug!(
   304→                "Blocked syscall in VM {}: {} (count: {})",
   305→                vm_id, syscall, count
   306→            );
   307→        }
   308→
   309→        Ok(())
   310→    }
   311→
   312→    /// Get all audit entries for a VM
   313→    pub async fn get_entries_for_vm(&self, vm_id: &str) -> Vec<SeccompAuditEntry> {
   314→        let entries = self.entries.read().await;
   315→        entries
   316→            .iter()
   317→            .filter(|e| e.vm_id == vm_id)
   318→            .cloned()
   319→            .collect()
   320→    }
   321→
   322→    /// Get recent entries (within last N seconds)
   323→    pub async fn get_recent_entries(&self, seconds: i64) -> Vec<SeccompAuditEntry> {
   324→        let cutoff = chrono::Utc::now() - chrono::Duration::seconds(seconds);
   325→        let entries = self.entries.read().await;
   326→        entries
   327→            .iter()
   328→            .filter(|e| e.timestamp > cutoff)
   329→            .cloned()
   330→            .collect()
   331→    }
   332→
   333→    /// Clear audit log (call when VM is destroyed)
   334→    pub async fn clear_vm(&self, vm_id: &str) {
   335→        let mut counts = self.violation_counts.write().await;
   336→        counts.remove(vm_id);
   337→
   338→        let mut entries = self.entries.write().await;
   339→        entries.retain(|e| e.vm_id != vm_id);
   340→    }
   341→
   342→    /// Get statistics
   343→    pub async fn get_stats(&self, vm_id: &str) -> SeccompStats {
   344→        let entries = self.entries.read().await;
   345→        let vm_entries: Vec<_> = entries.iter().filter(|e| e.vm_id == vm_id).collect();
   346→        let total_blocked = vm_entries.len() as u32;
   347→
   348→        // Count unique syscalls
   349→        let mut unique_syscalls: std::collections::HashSet<&str> = std::collections::HashSet::new();
   350→        for entry in &vm_entries {
   351→            unique_syscalls.insert(entry.syscall.as_str());
   352→        }
   353→
   354→        SeccompStats {
   355→            total_blocked,
   356→            unique_syscalls: unique_syscalls.len() as u32,
   357→        }
   358→    }
   359→}
   360→
   361→/// Seccomp statistics for a VM
   362→#[derive(Debug, Clone, Serialize, Deserialize)]
   363→pub struct SeccompStats {
   364→    /// Total number of blocked syscalls
   365→    pub total_blocked: u32,
   366→    /// Number of unique syscalls blocked
   367→    pub unique_syscalls: u32,
   368→}
   369→
   370→/// Validate seccomp rules before applying to VM
   371→pub fn validate_seccomp_rules(filter: &SeccompFilter) -> Result<()> {
   372→    // Ensure audit is enabled (security requirement)
   373→    if !filter.audit_enabled {
   374→        anyhow::bail!("Seccomp audit logging must be enabled for security");
   375→    }
   376→
   377→    // Validate that minimal profile has at least basic I/O syscalls
   378→    let whitelist = filter.build_whitelist();
   379→    let required = ["read", "write", "exit", "exit_group"];
   380→
   381→    for sys in required.iter() {
   382→        if !whitelist.contains(sys) {
   383→            anyhow::bail!(
   384→                "Seccomp filter missing required syscall: {} - VM will not function",
   385→                sys
   386→            );
   387→        }
   388→    }
   389→
   390→    info!(
   391→        "Seccomp filter validation passed: {} syscalls allowed",
   392→        whitelist.len()
   393→    );
   394→
   395→    Ok(())
   396→}
