    19→/// Firecracker VM process manager
    20→#[derive(Debug)]
    21→pub struct FirecrackerProcess {
    22→    pub pid: u32,
    23→    pub socket_path: String,
    24→    pub child_process: Option<Child>,
    25→    pub spawn_time_ms: f64,
    26→}
    27→
