    57→/// Start a Firecracker VM process
    58→pub async fn start_firecracker(config: &VmConfig) -> Result<FirecrackerProcess> {
    59→    let start_time = Instant::now();
    60→    info!("Starting Firecracker VM: {}", config.vm_id);
    61→
    62→    // 1. Validate resources
    63→    let kernel_path = PathBuf::from(&config.kernel_path);
    64→    let rootfs_path = PathBuf::from(&config.rootfs_path);
    65→
    66→    if !kernel_path.exists() {
    67→        return Err(anyhow!("Kernel image not found at: {:?}", kernel_path));
    68→    }
    69→    if !rootfs_path.exists() {
    70→        return Err(anyhow!("Root filesystem not found at: {:?}", rootfs_path));
    71→    }
    72→
    73→    // 2. Prepare socket path
    74→    let socket_path = format!("/tmp/firecracker-{}.socket", config.vm_id);
    75→    if Path::new(&socket_path).exists() {
    76→        tokio::fs::remove_file(&socket_path)
    77→            .await
    78→            .context("Failed to remove existing socket")?;
    79→    }
    80→
    81→    // 3. Spawn Firecracker process
    82→    let mut command = Command::new("firecracker");
    83→    command.arg("--api-sock").arg(&socket_path);
    84→
    85→    // Redirect stdout/stderr to null or log file to keep output clean
    86→    command.stdout(std::process::Stdio::null());
    87→    command.stderr(std::process::Stdio::null());
    88→
    89→    let mut child = command
    90→        .spawn()
    91→        .context("Failed to spawn firecracker process")?;
    92→    let pid = child
    93→        .id()
    94→        .ok_or_else(|| anyhow!("Failed to get firecracker PID"))?;
    95→
    96→    debug!("Firecracker process started with PID: {}", pid);
    97→
    98→    // 4. Wait for socket to be ready (retry loop)
    99→    let mut retries = 0;
   100→    let max_retries = 50; // 50 * 10ms = 500ms
   101→    let mut socket_ready = false;
   102→
   103→    while retries < max_retries {
   104→        if Path::new(&socket_path).exists() {
   105→            socket_ready = true;
   106→            break;
   107→        }
   108→        tokio::time::sleep(tokio::time::Duration::from_millis(10)).await;
   109→        retries += 1;
   110→    }
   111→
   112→    if !socket_ready {
   113→        // Kill the process if socket never appeared
   114→        let _ = child.kill().await;
   115→        return Err(anyhow!("Firecracker API socket did not appear in time"));
   116→    }
   117→
   118→    // 5. Connect to API and configure VM
   119→    if let Err(e) = configure_vm(&socket_path, config).await {
   120→        let _ = child.kill().await;
   121→        return Err(e);
   122→    }
   123→
   124→    // 6. Start the instance
   125→    if let Err(e) = start_instance(&socket_path).await {
   126→        let _ = child.kill().await;
   127→        return Err(e);
   128→    }
   129→
   130→    let elapsed = start_time.elapsed();
   131→    let spawn_time_ms = elapsed.as_secs_f64() * 1000.0;
   132→    info!("VM {} started in {:.2}ms", config.vm_id, spawn_time_ms);
   133→
   134→    Ok(FirecrackerProcess {
   135→        pid,
   136→        socket_path,
   137→        child_process: Some(child),
   138→        spawn_time_ms,
   139→    })
   140→}
   141→
   142→/// Stop a Firecracker VM process
   143→pub async fn stop_firecracker(mut process: FirecrackerProcess) -> Result<()> {
   144→    info!("Stopping Firecracker VM (PID: {})", process.pid);
   145→
   146→    // Try to send InstanceStart with "Exit" action? No, Send SendCtrlAltDel?
   147→    // Or just kill the process. Firecracker usually handles SIGTERM gracefully.
   148→
   149→    if let Some(mut child) = process.child_process.take() {
   150→        child
   151→            .kill()
   152→            .await
   153→            .context("Failed to kill firecracker process")?;
   154→    }
   155→
   156→    // Cleanup socket
   157→    if Path::new(&process.socket_path).exists() {
   158→        let _ = tokio::fs::remove_file(&process.socket_path).await;
   159→    }
   160→
   161→    Ok(())
   162→}
