   164→// Helper functions for API interaction
   165→
   166→async fn send_request<T: Serialize>(
   167→    socket_path: &str,
   168→    method: hyper::Method,
   169→    path: &str,
   170→    body: Option<&T>,
   171→) -> Result<()> {
   172→    // We create a new connection for each request for simplicity,
   173→    // though reusing it would be slightly faster.
   174→    // Given the low number of requests, this is acceptable.
   175→
   176→    let stream = UnixStream::connect(socket_path)
   177→        .await
   178→        .context("Failed to connect to firecracker socket")?;
   179→    let io = TokioIo::new(stream);
   180→    let (mut sender, conn) = hyper::client::conn::http1::handshake(io)
   181→        .await
   182→        .context("Handshake failed")?;
   183→
   184→    tokio::task::spawn(async move {
   185→        if let Err(err) = conn.await {
   186→            // It's expected that connection might close after response
   187→            debug!("Connection closed: {:?}", err);
   188→        }
   189→    });
   190→
   191→    let req_body = if let Some(b) = body {
   192→        let json = serde_json::to_string(b).context("Failed to serialize body")?;
   193→        Full::new(Bytes::from(json))
   194→    } else {
   195→        Full::new(Bytes::from(""))
   196→    };
   197→
   198→    let req = Request::builder()
   199→        .method(method)
   200→        .uri(format!("http://localhost{}", path)) // Host header is required but ignored for unix socket
   201→        .header("Content-Type", "application/json")
   202→        .header("Accept", "application/json")
   203→        .body(req_body)
   204→        .context("Failed to build request")?;
   205→
   206→    let res = sender
   207→        .send_request(req)
   208→        .await
   209→        .context("Failed to send request")?;
   210→
   211→    if res.status().is_success() || res.status() == StatusCode::NO_CONTENT {
   212→        Ok(())
   213→    } else {
   214→        let status = res.status();
   215→        let body_bytes = res.collect().await?.to_bytes();
   216→        let body_str = String::from_utf8_lossy(&body_bytes);
   217→        Err(anyhow!("Firecracker API error: {} - {}", status, body_str))
   218→    }
   219→}
