   221→async fn configure_vm(socket_path: &str, config: &VmConfig) -> Result<()> {
   222→    // 1. Set Boot Source
   223→    let boot_source = BootSource {
   224→        kernel_image_path: config.kernel_path.clone(),
   225→        boot_args: Some("console=ttyS0 reboot=k panic=1 pci=off".to_string()),
   226→    };
   227→    send_request(
   228→        socket_path,
   229→        hyper::Method::PUT,
   230→        "/boot-source",
   231→        Some(&boot_source),
   232→    )
   233→    .await
   234→    .context("Failed to configure boot source")?;
   235→
   236→    // 2. Set Rootfs Drive
   237→    let rootfs = Drive {
   238→        drive_id: "rootfs".to_string(),
   239→        path_on_host: config.rootfs_path.clone(),
   240→        is_root_device: true,
   241→        is_read_only: false,
   242→    };
   243→    send_request(
   244→        socket_path,
   245→        hyper::Method::PUT,
   246→        "/drives/rootfs",
   247→        Some(&rootfs),
   248→    )
   249→    .await
   250→    .context("Failed to configure rootfs")?;
   251→
   252→    // 3. Set Machine Config
   253→    let machine_config = MachineConfiguration {
   254→        vcpu_count: config.vcpu_count,
   255→        mem_size_mib: config.memory_mb,
   256→    };
   257→    send_request(
   258→        socket_path,
   259→        hyper::Method::PUT,
   260→        "/machine-config",
   261→        Some(&machine_config),
   262→    )
   263→    .await
   264→    .context("Failed to configure machine")?;
   265→
   266→    Ok(())
   267→}
   268→
   269→async fn start_instance(socket_path: &str) -> Result<()> {
   270→    let action = Action {
   271→        action_type: "InstanceStart".to_string(),
   272→    };
   273→    send_request(socket_path, hyper::Method::PUT, "/actions", Some(&action))
   274→        .await
   275→        .context("Failed to start instance")?;
   276→    Ok(())
   277→}
