     1→// vsock-based Host-Guest Communication
     2→//
     3→// This module implements secure communication between host and guest VMs
     4→// using vsock (Virtual Socket) protocol. vsock provides a lightweight,
     5→// low-latency communication channel that doesn't require traditional networking.
     6→//
     7→// Key invariants:
     8→// - No external network access required
     9→// - Low latency communication
    10→// - Secure by design (isolated communication channel)
    11→
    12→use anyhow::{Context, Result};
    13→use serde::{Deserialize, Serialize};
    14→use std::path::PathBuf;
    15→use tokio::fs;
    16→use tokio::io::{AsyncReadExt, AsyncWriteExt, BufReader};
    17→use tokio::net::{UnixListener, UnixStream};
    18→
    19→/// vsock communication protocol version
    20→pub const VSOCK_PROTOCOL_VERSION: u32 = 1;
    21→
    22→/// Maximum message size (16MB to prevent DoS)
    23→pub const MAX_MESSAGE_SIZE: usize = 16 * 1024 * 1024;
    24→
    25→/// vsock host listener
    26→#[derive(Debug)]
    27→pub struct VsockHostListener {
    28→    listener: UnixListener,
    29→    vm_id: String,
    30→}
    31→
    32→/// vsock client (guest side)
    33→#[derive(Debug)]
    34→pub struct VsockClient {
    35→    socket_path: PathBuf,
    36→}
    37→
    38→/// vsock message types
    39→#[derive(Debug, Clone, Serialize, Deserialize)]
    40→pub enum VsockMessage {
    41→    /// Request from guest to host
    42→    Request {
    43→        id: String,
    44→        method: String,
    45→        params: serde_json::Value,
    46→    },
    47→    /// Response from host to guest
    48→    Response {
    49→        id: String,
    50→        result: Option<serde_json::Value>,
    51→        error: Option<String>,
    52→    },
    53→    /// Notification (no response expected)
    54→    Notification {
    55→        method: String,
    56→        params: serde_json::Value,
    57→    },
    58→}
    59→
    60→impl VsockMessage {
    61→    /// Create a new request
    62→    pub fn request(id: String, method: String, params: serde_json::Value) -> Self {
    63→        Self::Request { id, method, params }
    64→    }
    65→
    66→    /// Create a new response
    67→    pub fn response(id: String, result: Option<serde_json::Value>, error: Option<String>) -> Self {
    68→        Self::Response { id, result, error }
    69→    }
    70→
    71→    /// Create a new notification
    72→    pub fn notification(method: String, params: serde_json::Value) -> Self {
    73→        Self::Notification { method, params }
    74→    }
    75→
    76→    /// Serialize message to JSON
    77→    pub fn to_json(&self) -> Result<Vec<u8>> {
    78→        serde_json::to_vec(self).context("Failed to serialize vsock message")
    79→    }
    80→
    81→    /// Deserialize message from JSON
    82→    pub fn from_json(data: &[u8]) -> Result<Self> {
    83→        // Enforce size limit to prevent DoS
    84→        if data.len() > MAX_MESSAGE_SIZE {
    85→            anyhow::bail!("Message size exceeds maximum allowed size");
    86→        }
    87→
    88→        serde_json::from_slice(data).context("Failed to deserialize vsock message")
    89→    }
    90→}
    91→
    92→/// Message handler trait for host-side processing
    93→#[async_trait::async_trait]
    94→pub trait VsockMessageHandler: Send + Sync {
    95→    /// Handle a request from the guest
    96→    async fn handle_request(
    97→        &self,
    98→        method: &str,
    99→        params: serde_json::Value,
   100→    ) -> Result<serde_json::Value>;
   101→
   102→    /// Handle a notification from the guest
   103→    async fn handle_notification(&self, method: &str, params: serde_json::Value) -> Result<()>;
   104→}
   105→
   106→/// Default handler that rejects all operations
   107→#[allow(dead_code)]
   108→struct DefaultHandler;
   109→
   110→#[async_trait::async_trait]
   111→impl VsockMessageHandler for DefaultHandler {
   112→    async fn handle_request(
   113→        &self,
   114→        method: &str,
   115→        _params: serde_json::Value,
   116→    ) -> Result<serde_json::Value> {
   117→        anyhow::bail!("Method '{}' not implemented", method);
   118→    }
   119→
   120→    async fn handle_notification(&self, method: &str, _params: serde_json::Value) -> Result<()> {
   121→        tracing::warn!("Received unhandled notification: {}", method);
   122→        Ok(())
   123→    }
   124→}
