   126→impl VsockHostListener {
   127→    /// Create a new vsock host listener
   128→    ///
   129→    /// # Arguments
   130→    ///
   131→    /// * `vm_id` - Unique identifier for the VM
   132→    ///
   133→    /// # Returns
   134→    ///
   135→    /// * `VsockHostListener` - Host-side listener
   136→    pub async fn new(vm_id: String) -> Result<Self> {
   137→        let socket_dir = "/tmp/ironclaw/vsock";
   138→        fs::create_dir_all(socket_dir)
   139→            .await
   140→            .context("Failed to create vsock directory")?;
   141→
   142→        let socket_path = format!("{}/{}.sock", socket_dir, vm_id);
   143→
   144→        // Delete socket file if it exists (from previous run)
   145→        if fs::metadata(&socket_path).await.is_ok() {
   146→            fs::remove_file(&socket_path)
   147→                .await
   148→                .context("Failed to remove existing socket file")?;
   149→        }
   150→
   151→        let listener = UnixListener::bind(&socket_path).context("Failed to bind vsock socket")?;
   152→
   153→        tracing::info!("vsock host listener created: {}", socket_path);
   154→
   155→        Ok(Self { listener, vm_id })
   156→    }
   157→
   158→    /// Accept incoming connection from guest
   159→    pub async fn accept(&self) -> Result<VsockConnection> {
   160→        let (socket, _addr) = self
   161→            .listener
   162→            .accept()
   163→            .await
   164→            .context("Failed to accept vsock connection")?;
   165→
   166→        tracing::info!("vsock connection accepted");
   167→
   168→        Ok(VsockConnection::new(socket))
   169→    }
   170→
   171→    /// Run the message handler loop
   172→    ///
   173→    /// This method runs indefinitely, handling messages from the guest.
   174→    /// It should be run in a separate task.
   175→    pub async fn run_handler<H>(self, handler: H) -> Result<()>
   176→    where
   177→        H: VsockMessageHandler + Clone + 'static,
   178→    {
   179→        loop {
   180→            match self.accept().await {
   181→                Ok(conn) => {
   182→                    let handler_clone = handler.clone();
   183→                    tokio::spawn(async move {
   184→                        if let Err(e) = conn.handle_messages(handler_clone).await {
   185→                            tracing::error!("Message handler error: {}", e);
   186→                        }
   187→                    });
   188→                }
   189→                Err(e) => {
   190→                    tracing::error!("Failed to accept connection: {}", e);
   191→                    // Continue accepting new connections
   192→                }
   193→            }
   194→        }
   195→    }
   196→
   197→    /// Get the socket path (for passing to guest)
   198→    pub fn socket_path(&self) -> String {
   199→        format!("/tmp/ironclaw/vsock/{}.sock", self.vm_id)
   200→    }
   201→}
