   203→/// vsock connection (bidirectional)
   204→pub struct VsockConnection {
   205→    socket: UnixStream,
   206→}
   207→
   208→impl VsockConnection {
   209→    /// Create a new vsock connection
   210→    fn new(socket: UnixStream) -> Self {
   211→        Self { socket }
   212→    }
   213→
   214→    /// Handle incoming messages
   215→    async fn handle_messages<H>(mut self, handler: H) -> Result<()>
   216→    where
   217→        H: VsockMessageHandler + 'static,
   218→    {
   219→        let mut reader = BufReader::new(&mut self.socket);
   220→
   221→        loop {
   222→            match Self::read_message(&mut reader).await {
   223→                Ok(Some(msg)) => {
   224→                    let response = match msg {
   225→                        VsockMessage::Request { id, method, params } => {
   226→                            match handler.handle_request(&method, params).await {
   227→                                Ok(result) => VsockMessage::response(id, Some(result), None),
   228→                                Err(e) => {
   229→                                    tracing::error!("Request handler error: {}", e);
   230→                                    VsockMessage::response(id, None, Some(format!("{:?}", e)))
   231→                                }
   232→                            }
   233→                        }
   234→                        VsockMessage::Notification { method, params } => {
   235→                            if let Err(e) = handler.handle_notification(&method, params).await {
   236→                                tracing::error!("Notification handler error: {}", e);
   237→                            }
   238→                            continue; // No response for notifications
   239→                        }
   240→                        VsockMessage::Response { .. } => {
   241→                            tracing::warn!("Unexpected response message from guest");
   242→                            continue;
   243→                        }
   244→                    };
   245→
   246→                    // Drop the reader borrow before writing
   247→                    drop(reader);
   248→                    Self::write_message(&mut self.socket, &response).await?;
   249→                    reader = BufReader::new(&mut self.socket);
   250→                }
   251→                Ok(None) => {
   252→                    // Connection closed
   253→                    tracing::info!("vsock connection closed");
   254→                    break;
   255→                }
   256→                Err(e) => {
   257→                    tracing::error!("Failed to read message: {}", e);
   258→                    break;
   259→                }
   260→            }
   261→        }
   262→
   263→        Ok(())
   264→    }
   265→
   266→    /// Read a message from the socket
   267→    async fn read_message<R>(reader: &mut BufReader<R>) -> Result<Option<VsockMessage>>
   268→    where
   269→        R: AsyncReadExt + Unpin,
   270→    {
   271→        // Read message length (4 bytes, big-endian)
   272→        let mut len_bytes = [0u8; 4];
   273→        let n = reader.read_exact(&mut len_bytes).await;
   274→        if n.is_err() {
   275→            return Ok(None); // Connection closed
   276→        }
   277→
   278→        let msg_len = u32::from_be_bytes(len_bytes) as usize;
   279→
   280→        // Enforce size limit
   281→        if msg_len > MAX_MESSAGE_SIZE {
   282→            anyhow::bail!("Message size exceeds maximum: {} bytes", msg_len);
   283→        }
   284→
   285→        // Read message body
   286→        let mut buffer = vec![0u8; msg_len];
   287→        reader.read_exact(&mut buffer).await?;
   288→
   289→        // Deserialize message
   290→        let msg = VsockMessage::from_json(&buffer)?;
   291→        Ok(Some(msg))
   292→    }
   293→
   294→    /// Write a message to the socket
   295→    async fn write_message<W>(writer: &mut W, msg: &VsockMessage) -> Result<()>
   296→    where
   297→        W: AsyncWriteExt + Unpin,
   298→    {
   299→        let data = msg.to_json()?;
   300→        let len = data.len() as u32;
   301→
   302→        // Write length prefix (4 bytes, big-endian)
   303→        writer.write_all(&len.to_be_bytes()).await?;
   304→
   305→        // Write message body
   306→        writer.write_all(&data).await?;
   307→        writer.flush().await?;
   308→
   309→        Ok(())
   310→    }
   311→}
