   335→/// vsock client connection (for sending messages from guest to host)
   336→pub struct VsockClientConnection {
   337→    socket: UnixStream,
   338→    next_id: u64,
   339→}
   340→
   341→impl VsockClientConnection {
   342→    /// Create a new client connection
   343→    fn new(socket: UnixStream) -> Self {
   344→        Self { socket, next_id: 1 }
   345→    }
   346→
   347→    /// Send a request and wait for response
   348→    pub async fn send_request(
   349→        &mut self,
   350→        method: &str,
   351→        params: serde_json::Value,
   352→    ) -> Result<serde_json::Value> {
   353→        let id = self.next_id.to_string();
   354→        self.next_id += 1;
   355→
   356→        let msg = VsockMessage::request(id.clone(), method.to_string(), params);
   357→
   358→        VsockConnection::write_message(&mut self.socket, &msg).await?;
   359→
   360→        // Wait for response
   361→        let mut reader = BufReader::new(&mut self.socket);
   362→        loop {
   363→            match VsockConnection::read_message(&mut reader).await? {
   364→                Some(VsockMessage::Response {
   365→                    id: resp_id,
   366→                    result,
   367→                    error,
   368→                }) => {
   369→                    if resp_id == id {
   370→                        if let Some(err) = error {
   371→                            anyhow::bail!("Request failed: {}", err);
   372→                        }
   373→                        return result.context("Response missing result");
   374→                    }
   375→                    // Ignore responses to other requests
   376→                }
   377→                Some(_) => {
   378→                    // Ignore other message types
   379→                }
   380→                None => {
   381→                    anyhow::bail!("Connection closed while waiting for response");
   382→                }
   383→            }
   384→        }
   385→    }
   386→
   387→    /// Send a notification (no response expected)
   388→    pub async fn send_notification(
   389→        &mut self,
   390→        method: &str,
   391→        params: serde_json::Value,
   392→    ) -> Result<()> {
   393→        let msg = VsockMessage::notification(method.to_string(), params);
   394→
   395→        VsockConnection::write_message(&mut self.socket, &msg).await?;
   396→        Ok(())
   397→    }
   398→}
