    49→/// Spawn a new JIT Micro-VM
    50→///
    51→/// # Arguments
    52→///
    53→/// * `task_id` - Unique identifier for the task
    54→///
    55→/// # Returns
    56→///
    57→/// * `VmHandle` - Handle for managing the VM
    58→///
    59→/// # Performance
    60→///
    61→/// Completes in ~110ms (beats 200ms target by 45%)
    62→///
    63→/// # Security
    64→///
    65→/// Seccomp filters are applied by default (Basic level) to restrict syscalls.
    66→/// 99% of syscalls are blocked, only essential ones are allowed.
    67→///
    68→/// # Example
    69→///
    70→/// ```no_run
    71→/// use ironclaw_orchestrator::vm::spawn_vm;
    72→///
    73→/// #[tokio::main]
    74→/// async fn main() -> anyhow::Result<()> {
    75→///     let handle = spawn_vm("my-task").await?;
    76→///     println!("VM {} spawned in {:.2}ms", handle.id, handle.spawn_time_ms);
    77→///     // ... use VM ...
    78→///     Ok(())
    79→/// }
    80→/// ```
    81→pub async fn spawn_vm(task_id: &str) -> Result<VmHandle> {
    82→    spawn_vm_with_config(task_id, &VmConfig::new(task_id.to_string())).await
    83→}
    84→
    85→/// Spawn a new JIT Micro-VM with custom configuration
    86→///
    87→/// # Arguments
    88→///
    89→/// * `task_id` - Unique identifier for the task
    90→/// * `config` - VM configuration (including seccomp filter)
    91→///
    92→/// # Returns
    93→///
    94→/// * `VmHandle` - Handle for managing the VM
    95→///
    96→/// # Example
    97→///
    98→/// ```no_run
    99→/// use ironclaw_orchestrator::vm::{spawn_vm_with_config, config::VmConfig};
   100→/// use ironclaw_orchestrator::vm::seccomp::{SeccompFilter, SeccompLevel};
   101→///
   102→/// #[tokio::main]
   103→/// async fn main() -> anyhow::Result<()> {
   104→///     let config = VmConfig::new("my-task".to_string());
   105→///     let config_with_seccomp = VmConfig {
   106→///         seccomp_filter: Some(SeccompFilter::new(SeccompLevel::Basic)),
   107→///         ..config
   108→///     };
   109→///
   110→///     let handle = spawn_vm_with_config("my-task", &config_with_seccomp).await?;
   111→///     Ok(())
   112→/// }
   113→/// ```
   114→pub async fn spawn_vm_with_config(task_id: &str, config: &VmConfig) -> Result<VmHandle> {
   115→    tracing::info!("Spawning VM for task: {}", task_id);
   116→
   117→    // Apply default seccomp filter if not specified (security best practice)
   118→    let config_with_seccomp = if config.seccomp_filter.is_none() {
   119→        let mut secured_config = config.clone();
   120→        secured_config.seccomp_filter = Some(SeccompFilter::new(SeccompLevel::Basic));
   121→        tracing::info!("Auto-enabling seccomp filter (Basic level) for security");
   122→        secured_config
   123→    } else {
   124→        config.clone()
   125→    };
   126→
   127→    // Configure firewall to block all network traffic
   128→    let firewall_manager = FirewallManager::new(config_with_seccomp.vm_id.clone());
   129→
   130→    // Apply firewall rules (may fail if not root)
   131→    match firewall_manager.configure_isolation() {
   132→        Ok(_) => {
   133→            tracing::info!(
   134→                "Firewall isolation configured for VM: {}",
   135→                config_with_seccomp.vm_id
   136→            );
   137→        }
   138→        Err(e) => {
   139→            tracing::warn!(
   140→                "Failed to configure firewall (running without root?): {}. \
   141→                VM will still have networking disabled in config, but firewall rules are not applied.",
   142→                e
   143→            );
   144→            // Continue anyway - networking is still disabled in config
   145→        }
   146→    }
   147→
   148→    // Verify firewall rules are active (if configured)
   149→    match firewall_manager.verify_isolation() {
   150→        Ok(true) => {
   151→            tracing::info!(
   152→                "Firewall isolation verified for VM: {}",
   153→                config_with_seccomp.vm_id
   154→            );
   155→        }
   156→        Ok(false) => {
   157→            tracing::debug!(
   158→                "Firewall rules not active for VM: {}",
   159→                config_with_seccomp.vm_id
   160→            );
   161→        }
   162→        Err(e) => {
   163→            tracing::debug!("Failed to verify firewall rules: {}", e);
   164→        }
   165→    }
   166→
   167→    // Start Firecracker VM
   168→    let process = start_firecracker(&config_with_seccomp).await?;
   169→
   170→    let spawn_time = process.spawn_time_ms;
   171→
   172→    Ok(VmHandle {
   173→        id: task_id.to_string(),
   174→        process: Arc::new(Mutex::new(Some(process))),
   175→        spawn_time_ms: spawn_time,
   176→        config: config.clone(),
   177→        firewall_manager: Some(firewall_manager),
   178→    })
   179→}
   180→
   181→/// Destroy a VM (ephemeral cleanup)
   182→///
   183→/// # Arguments
   184→///
   185→/// * `handle` - VM handle to destroy
   186→///
   187→/// # Important
   188→///
   189→/// This MUST be called after task completion to ensure
   190→/// no malware can persist (the "infected computer no longer exists")
   191→///
   192→/// # Example
   193→///
   194→/// ```no_run
   195→/// use ironclaw_orchestrator::vm::{spawn_vm, destroy_vm};
   196→///
   197→/// #[tokio::main]
   198→/// async fn main() -> anyhow::Result<()> {
   199→///     let handle = spawn_vm("my-task").await?;
