    15→async fn test_vm_rejects_networking_enabled() {
    16→    use crate::vm::config::VmConfig;
    17→
    18→    let mut config = VmConfig::new("test-networking".to_string());
    19→    config.enable_networking = true;
    20→
    21→    let result = config.validate();
    22→    assert!(result.is_err());
    23→    assert!(result.unwrap_err().to_string().contains("MUST be disabled"));
    24→}
    25→
    26→/// Test that multiple VMs can be spawned with unique firewall chains
    27→#[tokio::test]
    28→async fn test_multiple_vms_isolation() {
    29→    if !firecracker_available() {
    30→        return;
    31→    }
    32→    let handle1 = spawn_vm("task-1").await.unwrap();
    33→    let handle2 = spawn_vm("task-2").await.unwrap();
    34→
    35→    // Verify they have different IDs
    36→    assert_ne!(handle1.id, handle2.id);
    37→
    38→    // Verify they have different firewall chains
    39→    let chain1 = handle1.firewall_manager.as_ref().unwrap().chain_name();
    40→    let chain2 = handle2.firewall_manager.as_ref().unwrap().chain_name();
    41→    assert_ne!(chain1, chain2);
    42→
    43→    // Verify both have vsock paths
    44→    assert!(handle1.vsock_path().is_some());
    45→    assert!(handle2.vsock_path().is_some());
    46→
    47→    // Cleanup
    48→    destroy_vm(handle1).await.unwrap();
    49→    destroy_vm(handle2).await.unwrap();
    50→}
    51→
    52→/// Test that firewall rules are verified correctly
    53→#[tokio::test]
    54→async fn test_firewall_verification() {
    55→    if !firecracker_available() {
    56→        return;
    57→    }
    58→    let handle = spawn_vm("firewall-test").await.unwrap();
    59→
    60→    // Verify isolation (may be false if not running as root)
    61→    let isolated = verify_network_isolation(&handle);
    62→    assert!(isolated.is_ok());
    63→
    64→    // If iptables is available and running as root, isolation should be true
    65→    if isolated.is_ok() && isolated.unwrap() {
    66→        tracing::info!("Firewall isolation is active");
    67→    } else {
    68→        tracing::info!("Firewall isolation not configured (requires root)");
    69→    }
