   225→///     Ok(())
   226→/// }
   227→/// ```
   228→pub async fn destroy_vm(handle: VmHandle) -> Result<()> {
   229→    tracing::info!("Destroying VM: {}", handle.id);
   230→
   231→    #[cfg(unix)]
