        // Verify prefix matches (sanitization)
        assert!(chain.starts_with(expected_prefix), "Chain {} should start with {}", chain, expected_prefix);

        // Verify format (contains hash) and length
        assert!(chain.len() <= 28, "Chain name too long");
        assert_ne!(chain, expected_prefix, "Chain name should contain hash suffix");
    }
}

