    assert!(!chain.contains('$'));
    assert!(!chain.contains('%'));

    // Verify vsock path exists and is safe
    let vsock_path = handle.vsock_path().unwrap();
    assert!(vsock_path.contains("test-vm-123"));

    destroy_vm(handle).await.unwrap();
}

