    21‚Üí    }
    22‚Üí
    23‚Üí    /// Test that multiple VMs can be spawned with unique firewall chains
    24‚Üí    #[tokio::test]
    25‚Üí    async fn test_multiple_vms_isolation() {
    26‚Üí        let handle1 = spawn_vm("task-1").await.unwrap();
    27‚Üí        let handle2 = spawn_vm("task-2").await.unwrap();
    28‚Üí
    29‚Üí        // Verify they have different IDs
    30‚Üí        assert_ne!(handle1.id, handle2.id);
    31‚Üí
    32‚Üí        // Verify they have different firewall chains
    33‚Üí        let chain1 = handle1.firewall_manager.as_ref().unwrap().chain_name();
    34‚Üí        let chain2 = handle2.firewall_manager.as_ref().unwrap().chain_name();
    35‚Üí        assert_ne!(chain1, chain2);
    36‚Üí
    37‚Üí        // Verify both have vsock paths
    38‚Üí        assert!(handle1.vsock_path().is_some());
    39‚Üí        assert!(handle2.vsock_path().is_some());
    40‚Üí
    41‚Üí        // Cleanup
    42‚Üí        destroy_vm(handle1).await.unwrap();
    43‚Üí        destroy_vm(handle2).await.unwrap();
    44‚Üí    }
    45‚Üí
    46‚Üí    /// Test that firewall rules are verified correctly
    47‚Üí    #[tokio::test]
    48‚Üí    async fn test_firewall_verification() {
    49‚Üí        let handle = spawn_vm("firewall-test").await.unwrap();
    50‚Üí
    51‚Üí        // Verify isolation (may be false if not running as root)
    52‚Üí        let isolated = verify_network_isolation(&handle);
    53‚Üí        assert!(isolated.is_ok());
    54‚Üí
    55‚Üí        // If iptables is available and running as root, isolation should be true
    56‚Üí        if isolated.is_ok() && isolated.unwrap() {
    57‚Üí            tracing::info!("Firewall isolation is active");
    58‚Üí        } else {
    59‚Üí            tracing::info!("Firewall isolation not configured (requires root)");
    60‚Üí        }
    61‚Üí
    62‚Üí        destroy_vm(handle).await.unwrap();
    63‚Üí    }
    64‚Üí
    65‚Üí    /// Test that vsock paths are unique per VM
    66‚Üí    #[tokio::test]
    67‚Üí    async fn test_vsock_paths_are_unique() {
    68‚Üí        let handle1 = spawn_vm("vsock-unique-1").await.unwrap();
    69‚Üí        let handle2 = spawn_vm("vsock-unique-2").await.unwrap();
    70‚Üí
    71‚Üí        let path1 = handle1.vsock_path().unwrap();
    72‚Üí        let path2 = handle2.vsock_path().unwrap();
    73‚Üí
    74‚Üí        assert_ne!(path1, path2);
    75‚Üí        assert!(path1.contains("vsock-unique-1"));
