    80‚Üí    }
    81‚Üí
    82‚Üí    /// Test that VM config validation enforces security constraints
    83‚Üí    #[test]
    84‚Üí    fn test_config_validation_security() {
    85‚Üí        use crate::vm::config::VmConfig;
    86‚Üí
    87‚Üí        // Test 1: Networking must be disabled
    88‚Üí        let mut config = VmConfig::new("security-test-1".to_string());
    89‚Üí        config.enable_networking = true;
    90‚Üí        assert!(config.validate_anyhow().is_err());
    91‚Üí
    92‚Üí        // Test 2: vCPU count must be > 0
    93‚Üí        let mut config = VmConfig::new("security-test-2".to_string());
    94‚Üí        config.vcpu_count = 0;
    95‚Üí        assert!(config.validate_anyhow().is_err());
    96‚Üí
    97‚Üí        // Test 3: Memory must be at least 128 MB
    98‚Üí        let mut config = VmConfig::new("security-test-3".to_string());
    99‚Üí        config.memory_mb = 64;
   100‚Üí        assert!(config.validate_anyhow().is_err());
   101‚Üí
   102‚Üí        // Test 4: All constraints must be satisfied
   103‚Üí        let config = VmConfig::new("security-test-4".to_string());
   104‚Üí        assert!(config.validate_anyhow().is_ok());
   105‚Üí        assert!(!config.enable_networking);
