   109‚Üí
   110‚Üí    /// Test that firewall manager properly sanitizes VM IDs
   111‚Üí    #[test]
   112‚Üí    fn test_firewall_sanitizes_vm_ids() {
   113‚Üí        use crate::vm::firewall::FirewallManager;
   114‚Üí
   115‚Üí        let test_cases = vec![
