   117‚Üí            ("with-dash", "IRONCLAW_with_dash"),
   118‚Üí            ("with@symbol", "IRONCLAW_with_symbol"),
   119‚Üí            ("with/slash", "IRONCLAW_with_slash"),
   120‚Üí            ("with space", "IRONCLAW_with_space"),
