   129‚Üí
   130‚Üí    /// Test that vsock messages enforce size limits
   131‚Üí    #[test]
   132‚Üí    fn test_vsock_message_size_limit() {
   133‚Üí        use crate::vm::vsock::{VsockMessage, MAX_MESSAGE_SIZE};
   134‚Üí        use serde_json::json;
   135‚Üí
   136‚Üí        // Test 1: Normal-sized message works
   137‚Üí        let msg = VsockMessage::request(
   138‚Üí            "test-id".to_string(),
   139‚Üí            "test_method".to_string(),
