   278‚Üí        let _ = std::fs::File::create(&kernel_path);
   279‚Üí
   280‚Üí        let config = VmConfig {
   281‚Üí            kernel_path: kernel_path.to_str().unwrap().to_string(),
   282‚Üí            rootfs_path: "/non/existent/rootfs".to_string(),
   283‚Üí            ..VmConfig::default()
   284‚Üí        };
   285‚Üí        let result = start_firecracker(&config).await;
   286‚Üí        assert!(result.is_err());
   287‚Üí        assert!(result.unwrap_err().to_string().contains("Root filesystem not found"));
   288‚Üí
   289‚Üí        let _ = std::fs::remove_file(kernel_path);
