        destroy_vm(handle).await.unwrap();

        // Note: We can't verify the chain is deleted without root,
        // but the Drop trait ensures cleanup is attempted
        tracing::info!(
            "VM destroyed, firewall cleanup attempted for: {}",
            chain_name
        );
    }

