        let (kernel_path, rootfs_path) = create_test_resources().unwrap();

        let config = VmConfig {
            kernel_path,
            rootfs_path,
            ..VmConfig::new("cleanup-test".to_string())
        };

        let result = spawn_vm_with_config("cleanup-test", &config).await;

        let check_result = |r: &Result<VmHandle, anyhow::Error>| {
            r.is_ok() || r.as_ref().unwrap_err().to_string().contains("firecracker")
        };
        assert!(check_result(&result));

        if result.is_ok() {
            let handle = result.unwrap();
            let chain_name = handle
                .firewall_manager
                .as_ref()
                .unwrap()
                .chain_name()
                .to_string();

